// src/components/PricingSection.jsx
import React from 'react';
import { Check } from 'lucide-react';

const plans = [
  {
    name: 'Starter',
    price: '9,990',
    description: 'Lets you get started. Recommended for small fiction/poetry books.',
    isPopular: false,
    features: {
      'Cover Designing': [
        'Standard Cover Design',
        '1 Cover Sample',
        'Paperback cover',
        '2 Cover Revision Round',
        { disabled: true, text: 'Hardback cover' }
      ],
      'Interior Designing': [
        'Paperback & ebook',
        'B/W Interior Type',
        'Basic Interior Design',
        '2 Interior Revision Round',
        'Plain Text (Limit 150 Pages)',
        'Manuscript Type'
      ],
      'Distribution Service': [
        'India Online Distribution',
        'eBook Distribution',
        { disabled: true, text: 'International Online Distribution' },
        { disabled: true, text: 'International Distribution' },
        { disabled: true, text: 'Shop Distribution' }
      ],
      'Marketing Plans': [
        { disabled: true, text: 'Amazon Prime Placement (Add-On)' },
        'Pre-Orders (Add-On)',
        'Amazon Sponsored Ads (Add-On)',
        'Amazon Kindle Promotions (Add-On)',
        'AudioBook Publishing (Add-On)',
        { disabled: true, text: 'Custom Bookmarks' },
        { disabled: true, text: 'Online Press Release' }
      ],
      'Royalty & Support': [
        'Monthly Royalty Update',
        'Payout on Request',
        '100% Profit Share',
        'Project Manager',
        'Post-Publishing Support'
      ]
    }
  },
  // Add other plans similarly...
];

const PricingCard = ({ plan }) => {
  return (
    <div className={`bg-white rounded-lg shadow-lg overflow-hidden ${
      plan.isPopular ? 'border-2 border-[#F4511E]' : 'border border-gray-200'
    }`}>
      {plan.isPopular && (
        <div className="bg-[#F4511E] text-white text-center py-2">
          Most Popular Plan
        </div>
      )}
      <div className="p-6">
        <h3 className="text-2xl font-bold text-[#F4511E] mb-2">{plan.name}</h3>
        <p className="text-sm text-gray-600 mb-4">{plan.description}</p>
        <div className="mb-6">
          <span className="text-4xl font-bold">₹{plan.price}</span>
        </div>

        {Object.entries(plan.features).map(([category, items]) => (
          <div key={category} className="mb-6">
            <h4 className="text-[#F4511E] font-medium mb-2">{category}</h4>
            <ul className="space-y-2">
              {items.map((item, index) => {
                const isDisabled = typeof item === 'object' && item.disabled;
                const text = typeof item === 'object' ? item.text : item;

                return (
                  <li 
                    key={index}
                    className={`flex items-start ${isDisabled ? 'text-gray-400 line-through' : 'text-gray-700'}`}
                  >
                    <Check className="w-5 h-5 mr-2 flex-shrink-0 text-[#F4511E]" />
                    <span className="text-sm">{text}</span>
                  </li>
                );
              })}
            </ul>
          </div>
        ))}

        <button className={`w-full py-2 px-4 rounded-md transition-colors ${
          plan.isPopular
            ? 'bg-[#F4511E] text-white hover:bg-[#D84315]'
            : 'border border-gray-300 hover:bg-gray-50'
        }`}>
          GET STARTED
        </button>
      </div>
    </div>
  );
};

const PricingSection = () => {
  return (
    <section className="py-16 px-4 bg-gray-50">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <span className="inline-block px-4 py-2 bg-white rounded-full text-gray-800 text-sm mb-4">
            Book Publishing Services
          </span>
          <h2 className="text-4xl font-bold text-[#F4511E] mb-4">
            Everything you need to become a<br />
            Best Seller Author!
          </h2>
          <p className="text-gray-600 max-w-3xl mx-auto">
            At OrangeBooks Publication, you get the best options for self publishing services in India that helps you to self publish your book professionally. Complete set of publishing services. From designing book covers to publishing, promotion to distribution.
          </p>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
          {plans.map((plan) => (
            <PricingCard key={plan.name} plan={plan} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default PricingSection;
