import React from 'react';

const PaymentStats = () => {
  // Chart data
  const chartData = [
    { day: 'M', value: 33.21, total: 66.79 },
    { day: 'T', value: 42.11, total: 57.89 },
    { day: 'W', value: 36.69, total: 63.31 },
    { day: 'T', value: 40.17, total: 59.83 },
    { day: 'F', value: 43.65, total: 56.35 },
    { day: 'S', value: 37.85, total: 62.15 },
    { day: 'S', value: 39.79, total: 60.21 }
  ];

  // Payment methods data
  const paymentMethods = [
    { 
      method: 'Paypal', 
      percentage: 52, 
      color: '#0085DB',
      borderColor: '#0085DB',
      className: 'bg-blue-500'
    },
    { 
      method: 'Credit Card', 
      percentage: 48, 
      color: '#E7ECF0',
      borderColor: '#E7ECF0',
      className: 'bg-gray-200'
    }
  ];

  return (
    <div className="bg-white rounded-[18px] p-6 w-[350px] shadow-[0px_2px_6px_rgba(37,83,185,0.1)]">
      {/* Header Section */}
      <div className="flex justify-between items-start mb-6">
        <div>
          <h5 className="text-[18px] font-semibold text-[#111C2D] leading-[26px] mb-1">
            Payments
          </h5>
          <span className="text-[14px] text-[#111C2D] leading-[22px] font-normal">
            Last 7 days
          </span>
        </div>
        <div className="flex items-center gap-2">
          <span className="text-[18px] font-semibold text-[#111C2D] leading-[26px]">
            12,389
          </span>
          <div className="bg-[#FFF6EA] border border-[#F8C076] rounded-full px-2 py-1">
            <span className="text-[12px] text-[#F8C076] leading-[21px]">
              -3.08%
            </span>
          </div>
        </div>
      </div>

      {/* Chart Section */}
      <div className="mb-8">
        <div className="flex justify-between items-end h-[150px]">
          {chartData.map(({ day, value, total }) => (
            <div key={day} className="flex flex-col items-center w-[24px]">
              <div className="relative w-full h-[120px]">
                {/* Background bar */}
                <div 
                  className="absolute bottom-0 w-full bg-[#E7ECF0] rounded-sm opacity-85"
                  style={{ height: '100%' }}
                />
                {/* Value bar */}
                <div 
                  className="absolute bottom-0 w-full bg-[#0085DB] rounded-sm opacity-85"
                  style={{ height: `${total}%` }}
                />
              </div>
              <span className="mt-2 text-[12px] text-[#ADB0BB]">{day}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Payment Methods with Progress Bars */}
      <div className="space-y-4">
        {paymentMethods.map((method, index) => (
          <div key={method.method} className="space-y-2">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div 
                  className="w-4 h-4 rounded-full border-[1.33px]"
                  style={{ borderColor: method.borderColor }}
                />
                <span className="text-[14px] text-[#111C2D] leading-6">
                  {method.method}
                </span>
              </div>
              <span className="text-[14px] text-[#111C2D] leading-6">
                {method.percentage}%
              </span>
            </div>
            {/* Progress Bar */}
            <div className="w-full h-1 bg-gray-100 rounded-full overflow-hidden">
              <div 
                className={`h-full transition-all duration-500 ${method.className}`}
                style={{ 
                  width: `${method.percentage}%`,
                }}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PaymentStats;
