import React from 'react';

const TransactionItem = ({ iconSrc, title, subtitle, amount, currency }) => {
  return (
    <li className="d-flex align-items-center mb-6">
      <div className="avatar flex-shrink-0 me-3">
        <img src={iconSrc} alt={title} className="rounded" />
      </div>
      <div className="d-flex w-100 flex-wrap align-items-center justify-content-between gap-2">
        <div className="me-2">
          <small className="d-block">{subtitle}</small>
          <h6 className="fw-normal mb-0">{title}</h6>
        </div>
        <div className="user-progress d-flex align-items-center gap-2">
          <h6 className="fw-normal mb-0">{amount}</h6>
          <span className="text-muted">{currency}</span>
        </div>
      </div>
    </li>
  );
};

export default TransactionItem;
